mapper.select:
select count(*) from tb_saleplan
where delete_at = 0
and bizcd = #{bizcd}

mapper.insert:
insert
  into
    tb_saleplan(
       seq, 
       business_code, 
       bizcd, 
       subject, 
       instt_name, 
       instt_section, 
       product_section, 
       price, 
       discount, 
       sale_qy, 
       content, 
       writer_id, 
       writer_name, 
       consent, 
       request,
       delete_at, 
       register_date
    )
  values
    (  #{seq}
      ,#{business_code}
      ,#{bizcd}
      ,#{subject}
      ,#{instt_name}
      ,#{instt_section}
      ,#{product_section}
      ,#{price}
      ,#{discount}
      ,#{sale_qy}
      ,#{content}
      ,#{writer_id}
      ,#{writer_name}
      	,0
      	,0
      	,0
      ,now()
    )

mapper.update:
update tb_saleplan 
set
subject = #{subject}
,instt_section = #{instt_section}
,product_section = #{product_section}
,instt_name = #{instt_name}
,sale_qy = #{sale_qy}
,content = #{content}
,update_date = now() 
where seq=#{seq}

update tb_saleplan 
set 
request = #{request}
where seq=#{seq}
